import HeroBanner from '../components/HeroBanner';
// ... in HomePage return, just above Featured Products
<HeroBanner />
import React from 'react';
import ProductCard from '../components/ProductCard';
import products from '../data/products';
import { Box, Grid, Typography } from '@mui/material';

const HomePage = () => {
  const clothes = products.filter(p => p.category === 'clothes');
  const watches = products.filter(p => p.category === 'watches');
  return (
    <Box sx={{ padding: 2 }}>
      <HeroBanner />
      <Typography variant="h4" sx={{ marginBottom: 3, fontWeight: 'bold', color: '#1976d2' }}>
        Clothes
      </Typography>
      <Grid container spacing={3} sx={{ marginBottom: 6 }}>
        {clothes.map((product) => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={product.id}>
            <ProductCard product={product} />
          </Grid>
        ))}
      </Grid>
      <Typography variant="h4" sx={{ marginBottom: 3, fontWeight: 'bold', color: '#1976d2' }}>
        Watches
      </Typography>
      <Grid container spacing={3}>
        {watches.map((product) => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={product.id}>
            <ProductCard product={product} />
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default HomePage;
