import React from 'react';
import { Card, CardMedia, CardContent, Typography, CardActions, Button, Chip, Rating, Box } from '@mui/material';
import { Link } from 'react-router-dom';

const ProductCard = ({ product }) => {
  const handleAddToCart = () => {
    const cart = JSON.parse(localStorage.getItem('cart') || '[]');
    // Avoid duplicates
    const exists = cart.find(item => (item._id || item.id) === (product._id || product.id));
    if (!exists) {
      cart.push(product);
      localStorage.setItem('cart', JSON.stringify(cart));
      alert('Added to cart!');
    } else {
      alert('Product already in cart!');
    }
  };

  // Assign a random rating between 3.5 and 4.5 if not present
  const rating = product.rating || (Math.random() * (4.5 - 3.5) + 3.5);

  return (
    <Card
      sx={{
        maxWidth: 345, m: 2, transition: 'transform .2s, box-shadow .2s',
        '&:hover': { transform: 'scale(1.04)', boxShadow: 6 }
      }}
      elevation={2}
    >
      <CardMedia
        component="img"
        height="200"
        image={product.image}
        alt={product.name}
        sx={{ objectFit: 'contain', background: '#fafafa' }}
      />
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Typography gutterBottom variant="h6">{product.name}</Typography>
          {product.featured && <Chip label="Featured" color="secondary" size="small" sx={{ ml: 1 }} />}
        </Box>
        <Typography variant="body2" color="text.secondary" noWrap>{product.description}</Typography>
        <Rating value={rating} precision={0.1} readOnly sx={{ mt: 1 }} />
        <Typography variant="subtitle1" color="text.primary" sx={{ mt: 1 }}>${product.price.toFixed(2)}</Typography>
      </CardContent>
      <CardActions>
        <Button size="small" component={Link} to={`/product/${product._id || product.id}`}>View</Button>
        <Button size="small" color="primary" onClick={handleAddToCart}>Add to Cart</Button>
        <Button
          size="small"
          variant="contained"
          color="success"
          sx={{ ml: 1 }}
          onClick={() => alert('Proceed to buy!')}
        >
          Buy
        </Button>
      </CardActions>
    </Card>
  );
};

export default ProductCard;
